import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { LoginComponent } from './login/login.component';

const routes: Routes = [
  { path: '', redirectTo: '/componente-especifico', pathMatch: 'full' }, // Redirige a '/componente-especifico' cuando la ruta es ''
  { path: 'login', component: ComponenteEspecificoComponent }, // Configura la ruta para el componente específico
  // ... otras rutas de tu aplicación
];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule]
})
export class AppRoutingModule { }
